document.body.innerHTML+="<div id='mycrypto-footer'><div><img src='https://mycrypto.com/common/assets/images/logo-mycrypto.svg?2adf3b' /></div><div><div class='option' id='balance'></div><div class='option' id='network'></div></div></div>";var footerStyle=document.createElement("style");footerStyle.innerHTML='#mycrypto-footer {background-color: #163150;width: 100vw;height: 70px;position: fixed;bottom: 0;}#mycrypto-footer > div:first-child {height: 100%;width: 200px;float: left;text-align: center;line-height: 75px;}#mycrypto-footer > div:first-child > img {width: 150px;vertical-align: middle;}#mycrypto-footer > div:last-child {height: 100%;width: calc(100% - 220px);float: left;text-align: right;}.option {color: #fff;font-family: "Segoe UI";border: 1px solid #4d5f74;width: 125px;float: right;text-align: center;height: 37.5px;line-height: 37.5px;border-radius: 25px;font-size: 17px;margin-left: 5px;margin-right: 5px;margin-top: 18.75px;}',document.head.appendChild(footerStyle),window.addEventListener("load",async()=>{if(window.ethereum){window.web3=new Web3(ethereum);try{function updateEther(){web3.eth.getCoinbase(function(err,account){web3.eth.getBalance(account,function(err,balance){document.getElementById("balance").innerHTML=web3.fromWei(balance,"ether").toNumber().toFixed(4)+" ETH"})})}function updateNetwork(data){switch(data.networkVersion){case"1":document.getElementById("network").innerHTML="Mainnet";break;case"3":document.getElementById("network").innerHTML="Ropsten";break;case"4":document.getElementById("network").innerHTML="Rinkeby";break;case"42":document.getElementById("network").innerHTML="Kovan";break;case"5":document.getElementById("network").innerHTML="Goerli";break;default:document.getElementById("network").innerHTML="Unknown"}}function callback(data){updateEther(),updateNetwork(data)}await ethereum.enable(),web3.currentProvider.publicConfigStore.on("update",callback)}catch(error){}}else window.web3?window.web3=new Web3(web3.currentProvider):(document.getElementById("network").innerHTML="Unknown",document.getElementById("balance").innerHTML="Unknown")});